import axios from 'axios';
//Get SearchForm Element
const searchForm = document.querySelector('#search-form');
//Submit Search form
searchForm.addEventListener('submit',fetchMovie);

//fetch movie data from OMDB API
function fetchMovie(e){
    e.preventDefault();
    
    const searchText = document.querySelector('#search-text').value;
    
    const fetchUrl =`http://www.omdbapi.com/?i=tt3896198&apikey=3f6a0e17&s=${searchText}&type=movie&page=1`;

    axios.get(fetchUrl)
    .then(response => {
        var ms = response.data;
        if(ms.Response){
            showMovie(ms.Search);
        }
    })
    .catch(error => {
        console.log(error);
    });
    // fetchJsonp(fetchUrl)
    // .then(res=>res.json())
    // .then(data=>showMovie(data))
    // .catch(err =>console.log(err));
}
function showMovie(movies){
    console.log(movies);
    var result = document.getElementById('result');

    result.innerHTML == "";
    //Clear 
    for (let i = 0; i < movies.length; i++) {
        const movie = movies[i]; 
        
        var div = document.createElement('div');
        div.classList.add("card")
        div.innerHTML =`    
            <div class="card-body">
                <h3 class="card-header">${movie.Title}</h3>
                <ul class="list-group list-group-flush">
                    <li class="list-group-item">${movie.Year}</li>
                    <li class="list-group-item"><img src="${movie.Poster}" /></li>
                </ul>
                <div class="card-body">
                    <button class="btn btn-primary rounded-pill" name="nomination" value="${movie.imdbID}" mname="${movie.Title}">Nomination</button>
                    <button class="btn btn-primary rounded-pill" name="nomcancel" value="${movie.imdbID}">Cancel</button>
                </div>
            </div>
            
            `;
            result.appendChild(div);
   
           
    }
    document.getElementsByName("nomination").forEach(item=>{
        item.addEventListener('click',(e)=>{
            console.log(e.target.value);
            var list = document.getElementById('nominationList');
            var li = document.createElement('li');
            li.innerHTML = `<li class="list-group-item d-flex justify-content-between align-items-center">
                            ${e.target.attributes["mname"].value}
                            <button class="btn btn-primary rounded-pill">Cancel</button>
                        </li>`;
            list.appendChild(li);
            e.target.disabled = true;
        })
    })
}

